<template>
    <div class="channel">
        <group>
            <template v-for="(item,key) in channels">
                <cell :style="item.id == channel_id?'background-color:#ddd':''" :key="key"  :disabled="item.status!=1" :link="url+'?channel_id='+item.id+'&channel_name='+item.name+'&count_fee='+item.count_fee+'&rate_fee='+item.rate_fee+'&can_select_shop='+item.can_select_shop+'&channel_key='+item.channel_key">
                <img slot="icon" width="60" style="display:block;margin-right:5px;" src="data:image/svg+xml;base64,">
                <div slot="title">
                    <span>{{item.name}}</span>
                    <span style="color:#1A73E8;float: right;">{{(item.rate_fee*100).toFixed(2)||0}}%+{{item.count_fee||0}}元 </span>
                </div>
                <div slot="inline-desc">
                    <badge :text="'单笔'+item.max_amount"></badge><badge v-if="item.can_select_shop==1" text="支持自选商户"></badge>
                </div>
                </cell>
            </template>
        </group>
    </div>
</template>
<script>
import {
    Cell,
    Card,
    Group,
    Badge 
} from 'vux'
import { mapGetters, mapActions } from 'vuex'

export default {
    name: 'cash',
    data() {
        return {
            url:'',
            channel_id: '',
        }
    },
    components: {
        Cell,
        Card,
        Group,
        Badge 
    },
    computed: {  
        ...mapGetters(['channels']),
    },
    created() {
        this.$store.commit('updatePageTitle', '快捷支付');
        this.url = this.$route.query.url;
        this.channel_id = this.$route.query.channel_id;
        this.loadChannels();
    },
    methods: {
        filterTime(begin_time,end_time){
            let _begin_time;
            let _end_time;
            if(begin_time==0 && end_time==0){
                return `00:00-23:59`;
            }else{
                if(begin_time>0&&begin_time.toString().length>2){
                    _begin_time = begin_time.toString().slice(0,-2)+':'+begin_time.toString().slice(-2)
                }else{
                    _begin_time = `00:00`;
                }
                if(end_time>0&&end_time.toString().length>2){
                    _end_time = end_time.toString().slice(0,-2)+':'+end_time.toString().slice(-2)
                }else{
                    _end_time = `00:00`;
                }
                return _begin_time+'-'+_end_time;
            }
        },
        desc(limit_amount,max_amount){
            return `消费金额:${(limit_amount*1).toFixed(0)}-${(max_amount*1).toFixed(0)}`;
        },
        loadChannels() {
            this.$axios.get('/api/channels').then((res) => {
                this.$store.commit('updateChannels',res.data)
            })
        }
    },
    watch: {
    }
}
</script>
<style>
.channel .weui-cells{
    font-size: 16px;
}
.vux-badge{
    margin-right: 0.5em;
    background: #aaa!important;
}
</style>
